mapper.insert:
insert into t_order (id, user_id, product_id,
  status, create_time, update_time
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{productId,jdbcType=INTEGER}, 
  #{status,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update t_order
set status = #{status, jdbcType=VARCHAR}
where id = #{orderId, jdbcType=INTEGER}

mapper.select:
SELECT t1.id , t1.user_id , t1.product_id , t1.status , t1.create_time , t1.update_time , t2.real_name , t2.phone , t2.register_time , t3.name product_name , t3.budget FROM t_order t1 LEFT JOIN t_user t2 ON t1.user_id = t2.id LEFT JOIN t_product t3 ON t1.product_id = t3.id WHERE 1=1
and t1.product_id = #{productId}
and t1.user_id = #{userId}
