mapper.select:
select

from t_product
where 1=1

  and owner_id = #{ownerId,jdbcType=INTEGER}


  and name like concat('%',#{productName},'%')


  and publish_status = #{publishStatus}


  and status = #{status}


  and create_time >= #{publishTimeStart}


  and create_time <= #{publishTimeEnd}

select

from t_product
where 1=1

  and name like concat('%',#{productName},'%')


  and status = #{status}


  and status in('3','4')


  and real_delivery_time >= #{deliveryTimeStart}


  and real_delivery_time <= #{deliveryTimeEnd}

mapper.update:
update t_product
set status = #{status}
where id = #{productId}
