mapper.select:
select

from t_user
where 1=1

   and phone = #{phone,jdbcType=VARCHAR}


  and status = #{status,jdbcType=VARCHAR}


   and register_time >=  #{registerTimeStart,jdbcType=TIMESTAMP}


  and register_time <= #{registerTimeEnd,jdbcType=TIMESTAMP}


  and pass_time >= #{passTimeStart,jdbcType=TIMESTAMP}


  and pass_time <= #{passTimeEnd,jdbcType=TIMESTAMP}

mapper.update:
update t_user
set status = #{status}
where id = #{userId}
